function getCookie(e){var o=null;if(document.cookie&&""!==document.cookie)for(var t=document.cookie.split(";"),n=0;n<t.length;n++){var i=t[n].trim();if(i.substring(0,e.length+1)===e+"="){o=decodeURIComponent(i.substring(e.length+1));break}}return o}function Ajax(e){$.ajaxSetup({beforeSend:function(e,o){/^(GET|HEAD|OPTIONS|TRACE)$/.test(o.type)||this.crossDomain||e.setRequestHeader("X-CSRFToken",getCookie("csrftoken"))}}),$.ajax(e)}